import React, { FC, useState, useRef } from 'react';
import styled, { keyframes, css } from 'styled-components';
import useComponentSize from '@rehooks/component-size';
import { Animated } from 'react-animated-css';
import { cover } from 'polished';
import theme from '../theme';
import { fadeInBuilder } from '../utils';
import Layout from '../components/Layout';
import IntroCard from '../components/IntroCard';
import Loader from '../components/Loader';
import SocialMenu from '../components/SocialMenu';
import About from '../components/About';

const LARGE_MIN_SIZE = 760;

const LoaderOverlay = styled(Loader)`
  ${cover()}
`;

const FadeIn = styled.div`
  ${fadeInBuilder(2)}
`;

const SocialMenuHolder = styled(SocialMenu)`
  width: 100%;
  justify-content: center;
  margin-top: ${theme.spacing(4)};
`;

const Middle = styled.div`
  width: 100vw;
  height: 100vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  position: fixed;
  top: 0;
  text-align: center;
  z-index: 1;
`;

const OverlayHolder = styled.div`
  background: ${theme.palette.background.secondary};
`;

enum MenuType {
  About = 'about',
  Portfolio = 'portfolio',
}

const PageButton = styled.div`
  width: 10vw;
  height: 10vw;
  text-align: center;
  font-size: 25px;
  transform: rotate(-90deg);
  background: transparent;
  position: fixed;
  left: 0;
  bottom: 40vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  z-index: 4;
  transition: 0.4s ease-in-out;
  border-radius: 0px 0px 100px 100px;
`;

const Home: FC = () => {
  // const holderRef = useRef(null);
  // const { width: holderWidth } = useComponentSize(holderRef);
  const [isLoaded, setIsLoaded] = useState(false);
  const [activeOverlay, setActiveOverlay] = useState(null);
  const [hasAnimationEnd, setHasAnimationEnd] = useState(false);
  // const isSmall = holderWidth < LARGE_MIN_SIZE;

  //ref={holderRef}
  return (
    <>
      {!hasAnimationEnd && (
        <LoaderOverlay
          fadeOutTime={3000}
          onLoaded={() => setIsLoaded(true)}
          onAnimationEnd={() => setHasAnimationEnd(true)}
        />
      )}
      <Layout>
        {isLoaded && hasAnimationEnd && (
          <FadeIn>
            <Middle>
              <IntroCard
                name="Ricardo Rodrigues"
                description="Front End Developer"
              />
              <SocialMenuHolder />
            </Middle>
            <Animated
              animationIn="slideInLeft"
              animationOut="slideOutLeft"
              animationInDuration={1000}
              animationOutDuration={1000}
              isVisible={MenuType.About === activeOverlay}
            >
              <About onClose={() => setActiveOverlay(null)} />
            </Animated>
            <PageButton onClick={() => setActiveOverlay(null)} >ABOUT</PageButton>
          </FadeIn>
        )}
      </Layout>
    </>
  );
};

export default Home;
