import { keyframes, css } from 'styled-components';
export function fadeInBuilder(duration, delay = 0) {
  const fadeIn = keyframes(["from{opacity:0;}to{opacity:1;}"]);
  return css(["opacity:0;animation:", " ", "s ease-in-out forwards;animation-delay:", "s;"], fadeIn, duration, delay);
}
export function fadeOutBuilder(duration, delay = 0) {
  const fadeIn = keyframes(["from{opacity:1;}to{opacity:0;}"]);
  return css(["opacity:1;animation:", " ", "s ease-in-out forwards;animation-delay:", "s;"], fadeIn, duration, delay);
}
export function expandBuilder(duration) {
  const expandModal = keyframes(["from{transform:scale(1,0);}to{transform:scale(1,1);}"]);
  return css(["animation:", " ", "s ease-in-out;"], expandModal, duration);
}
