import React, { FC } from 'react';
import styled from 'styled-components';
import Particles, {MoveDirection, OutMode, HoverMode, ClickMode} from 'react-particles-js';
import theme from '../../theme';
import Logo from '../Logo';

const Holder = styled.div`
  max-width: 1200px;
  margin-right: auto;
  margin-left: auto;
`;

const Content = styled.div`
  margin-top: 40px;
  margin-bottom: 40px;
`;

const Layout: FC = ({children}) => (
  <>
    <Particles
      params={{
        particles: {
          number: {
            value: 30,
            density: {
              enable: false
            }
        },
        color: {
          value: theme.palette.primary.main
        },
        size: {
          value: 4,
          random: true,
          anim: {
            speed: 3,
            size_min: 0.3
          }
        },
        line_linked: {
          enable: false
        },
        move: {
          random: true,
          speed: 1,
          direction: MoveDirection.top,
          out_mode: OutMode.bounce
        }
      },
        "interactivity": {
            "events": {
                "onhover": {
                    "enable": true,
                    "mode": HoverMode.slow
                },
                "onclick": {
                    "enable": true,
                    "mode": ClickMode.repulse
                }
            },
            "modes": {
                "bubble": {
                    "distance": 250,
                    "duration": 2,
                    "size": 0,
                    "opacity": 0
                },
                "repulse": {
                    "distance": 400,
                    "duration": 4
                }
            }
        }
    }}
    />
    <Holder>
      <Logo />
      <Content>
        {children}
      </Content>
    </Holder>
  </>
);

export default Layout;
