import React, { FC, useState, useRef } from 'react';
import styled, { keyframes, css } from 'styled-components';
import {transparentize} from 'polished';
import useComponentSize from '@rehooks/component-size';
import { cover } from 'polished';
import theme from '../theme';
import { fadeInBuilder } from '../utils';
import Layout from '../components/Layout';
import IntroCard from '../components/IntroCard';
import Loader from '../components/Loader';
import SocialMenu from '../components/SocialMenu';
import OverlayWithAnimation from '../components/OverlayWithAnimation';
import About from '../components/About';

const LoaderOverlay = styled(Loader)`
  ${cover()}
`;

const FadeIn = styled.div`
  ${fadeInBuilder(2)}
`;

const SocialMenuHolder = styled(SocialMenu)`
  width: 100%;
  justify-content: center;
  margin-top: ${theme.spacing(4)};
`;

const Middle = styled.div`
  width: 100vw;
  height: 100vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  position: fixed;
  top: 0;
  text-align: center;
  z-index: 1;
`;

enum MenuType {
  About = 'about',
  Portfolio = 'portfolio',
}

interface IPageButton {
  isSmall: boolean;
}

const Menu = styled.div`
  margin-top: 6vh;
`;

const PageButton = styled.div<IPageButton>`
  transition: .4s ease-in-out;
  transition-property: color, background;
  z-index: 4;
  font-size: 25px;
  display: inline-block;

  ${({isSmall}) => {
    console.log({isSmall})
    if (isSmall) {
      return css`
        padding: 0 6%;
        font-weight: bold;
        text-decoration: underline;
        &:hover {
          color: ${theme.palette.button.primary.hover};
        }
      `;
    }

    return css`
      width: 10vw;
      height: 10vw;
      text-transform: uppercase;
      text-align: center;
      transform: rotate(-90deg);
      background: transparent;
      position: absolute;
      left: 0;
      bottom: 40vh;
      display: flex;
      flex-direction: column;
      justify-content: center;
      border-radius: 0px 0px 100px 100px;
      &:hover {
        background: ${transparentize(0.1, theme.palette.background.secondary)};
        color: ${theme.palette.button.secondary.normal};
      }
    `;
  }}
  &:hover {
    cursor: pointer;
  }
`;

const PageButton2 = styled.div<IPageButton>`
  transition: .4s ease-in-out;
  transition-property: color, background;
  z-index: 4;
  font-size: 25px;
  display: inline-block;

  ${({isSmall}) => {
    console.log({isSmall})
    if (isSmall) {
      return css`
        padding: 0 6%;
        font-weight: bold;
        text-decoration: underline;
        &:hover {
          color: ${theme.palette.button.primary.hover};
        }
      `;
    }

    return css`
      width: 10vw;
      height: 10vw;
      text-transform: uppercase;
      text-align: center;
      transform: rotate(90deg);
      background: transparent;
      position: absolute;
      right:0;
      bottom: 40vh;
      display: flex;
      flex-direction: column;
      justify-content: center;
      border-radius: 0px 0px 100px 100px;
      &:hover {
        background: ${transparentize(0.1, theme.palette.background.secondary)};
        color: ${theme.palette.button.secondary.normal};
      }
    `;
  }}
  &:hover {
    cursor: pointer;
  }
`;

const Home: FC = () => {
  const holderRef = useRef(null);
  const { width: holderWidth } = useComponentSize(holderRef);
  const [isLoaded, setIsLoaded] = useState(false);
  const [activeOverlay, setActiveOverlay] = useState<MenuType|null>(null);
  const [hasAnimationEnd, setHasAnimationEnd] = useState(false);
  const isSmall = holderWidth < theme.breakpoints.sm;

  return (
    <div ref={holderRef}>
      {!hasAnimationEnd && (
        <LoaderOverlay
          fadeOutTime={3000}
          onLoaded={() => setIsLoaded(true)}
          onAnimationEnd={() => setHasAnimationEnd(true)}
        />
      )}
      <Layout>
        {isLoaded && hasAnimationEnd && (
          <FadeIn>
            <Middle>
              <IntroCard
                name="Ricardo Rodrigues"
                description="Front End Developer"
              />
              <SocialMenuHolder />
              <Menu>
                <PageButton isSmall={isSmall} onClick={() => setActiveOverlay(MenuType.About)}>About</PageButton>
                <PageButton2 isSmall={isSmall} onClick={() => setActiveOverlay(MenuType.Portfolio)}>Work</PageButton2>
              </Menu>
            </Middle>
            <OverlayWithAnimation
              title={'About'}
              isVisible={MenuType.About === activeOverlay}
              onClose={() => setActiveOverlay(null)}
            >
              <About />
            </OverlayWithAnimation>
          </FadeIn>
        )}
      </Layout>
    </div>
  );
};

export default Home;
