import React, {FC} from 'react';
import styled from 'styled-components';
import { IconContext } from "react-icons";
import { FaTwitter, FaEnvelope, FaGithub } from 'react-icons/fa';
import theme from '../../theme';
import SocialIconLink,  {IProps as ISocialIconLinkProps} from '../SocialIconLink';

interface IProps {
  className?: string;
}

interface ISocialData {
  url: string;
  icon: ISocialIconLinkProps['iconName']
}

const SOCIAL_DATA = [
  {
    url: 'https://github.com/RicardoRodrigues',
    icon: 'FaGithub'
  },
  {
    url: 'https://twitter.com/r11rodrigues',
    icon: 'FaTwitter'
  },
  {
    url: 'mailto:ricardo@rodriguez.pt',
    icon: 'FaEnvelope'
  }
] as ISocialData[];


const Holder = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  text-align: center;
`;


const SocialMenu: FC<IProps> = ({className}) => (
  <Holder>
    {SOCIAL_DATA.map(({icon, url}) => <SocialIconLink key={icon} iconName={icon} url={url} size="32" />)}
  </Holder>
)

export default SocialMenu;
