import React, { FC, useEffect, useState } from 'react';
import styled, {keyframes, css} from 'styled-components';
import {cover} from 'polished';
import Logo from '../Logo';

function glowBuilder(color: string) {
  const glow = keyframes`
    0% {
      box-shadow: 0 0 ${color};
      opacity: 0;
    }
    70% {
      transform: scale(1);
      box-shadow: 0 0 100px 80px ${color};
      opacity: 0.7;
    }
    100% {
      transform: scale(1);
      box-shadow: 0 0 50px 40px ${color};
      opacity: 0.5;
    }
  `;

  return glow;
}

function fadeInBuilder(duration: number, delay = 0) {
  const fadeIn = keyframes`
    from {
      opacity: 0;
    }
    to {
      opacity: 1;
    }
  `;

  return css`
    opacity: 0;
    animation: ${fadeIn} ${duration}s ease-in-out forwards;
    animation-delay: ${delay}s;
  `;
}

function fadeOutBuilder(duration: number, delay = 0) {
  const fadeIn = keyframes`
    from {
      opacity: 1;
    }
    to {
      opacity: 0;
    }
  `;

  return css`
    opacity: 1;
    animation: ${fadeIn} ${duration}s ease-in-out forwards;
    animation-delay: ${delay}s;
  `;
}

function expandBuilder(duration: number) {
  const expandModal = keyframes`
    from {
      transform: scale(1, 0);
    }
    to {
      transform: scale(1, 1);
    }
  `;

  return css`
    animation: ${expandModal} ${duration}s ease-in-out;
  `;
}
interface IHolder {
  isLoading: boolean;
}
const Holder = styled.div<IHolder>`
  position: relative;
  ${({isLoading}) => isLoading ? fadeInBuilder(1) : fadeOutBuilder(1)}
`;


const LogoWithAnimation = styled(Logo)`
  ${cover()}
  ${glowBuilder('red')}
  ${expandBuilder(2)}
`;

interface IProps {
  className?: string;
  showLogo?: boolean;
  onAnimationEnd?: () => void;
}

const Loader: FC<IProps> = ({className, onAnimationEnd}) => {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      setIsLoading(false);
    }, 30000);
  }, []);

  useEffect(() => {
    if (!isLoading) {
      setTimeout(() => {
        onAnimationEnd && onAnimationEnd()
      }, 1000);
    }
  }, [isLoading]);

   return <Holder className={className} isLoading={isLoading}><LogoWithAnimation /></Holder>;

};

export default Loader;
