import React, {FC} from 'react';
import styled from 'styled-components';
import theme from '../../theme';
import ArrowButton from '../ArrowButton';

interface IProps {
  className?: string;
  onClose: () => void
}

const Holder = styled.div`
  text-align: center;
`;


const About: FC<IProps> = ({className, onClose}) => (
  <Holder className={className}>
    <ArrowButton direction="Left" onClick={onClose} />
    <h4>About</h4>
    <p>I'm Ricardo and come from small island on Atlantic ocean. Called Madeira, Island of flowers... or where Cristiano Ronaldo is from.</p>
    A passionate for new emerging technologies such as HTML5 and CSS3.
Born in Venezuela but spent most of my life on Madeira Island, Portugal.
I can characterize me for my persistence, looking for new emerging technologies. I like to work in team.

  Random facts:
  - Don't drink or smoke, but love Coca-Cola
  - Love to travel and know new people and culture
  - Love Basketball and Footbal, as practice too
  - Can't be too much far swimm in some nice beach and sun
  </Holder>
)

export default About;
