import React, { FC, useState } from 'react';
import styled, {keyframes, css} from 'styled-components';
import {cover} from 'polished';
import theme from '../theme';
import {fadeInBuilder} from '../utils';
import Layout from '../components/Layout';
import IntroCard from '../components/IntroCard';
import Loader from '../components/Loader';

const LoaderOverlay = styled(Loader)`
  ${cover()}
`;

const IntroCardWithAnimation = styled(IntroCard)`
  ${fadeInBuilder(2)}
`;

const Home: FC = () => {
  const [isLoaded, setIsLoaded] = useState(false);
  const [hasAnimationEnd, setHasAnimationEnd] = useState(false);
  return (
    <>
      {!hasAnimationEnd && (
        <LoaderOverlay
          fadeOutTime={2000}
          onLoaded={() => setIsLoaded(true)}
          onAnimationEnd={() => setHasAnimationEnd(true)}
        />
      )}
      <Layout>
        { isLoaded && (
          <IntroCardWithAnimation 
            name="Ricardo Rodrigues"
            description="Front End Developer"
          />
        )}
      </Layout>
    </>
  );
};

export default Home;
