import React, {FC} from 'react';
import { Helmet } from 'react-helmet';

interface IMetatagas {
  lang?: string;
}

const METADATA = {
  title: 'Ricardo Rodrigues | Personal Portfolio Ricardo Rodrigues',
  description: 'Personal Portfolio Ricardo Rodrigues',
  keyWords: ['Ricardo', 'Rodrigues', 'frontend', 'curriculum', 'portfolio'],
  author: 'Ricardo Rodrigues',
  twitter: 'r11rodrigues',
}

const MetaTags: FC<IMetatagas> = ({lang = 'en'}) => (
  <Helmet
    htmlAttributes={{
      lang,
    }}
    title={METADATA.title}
    meta={[
      {
        name: `description`,
        content: METADATA.description,
      },
      {
        property: `og:title`,
        content: METADATA.title,
      },
      {
        property: `og:description`,
        content: METADATA.description,
      },
      {
        property: `og:type`,
        content: `website`,
      },
      {
        name: `twitter:card`,
        content: `summary`,
      },
      {
        name: `twitter:creator`,
        content: METADATA.author,
      },
      {
        name: `twitter:title`,
        content: METADATA.author,
      },
      {
        name: `twitter:description`,
        content: METADATA.description,
      },
    ]
      .concat(
        METADATA.keyWords.length > 0
          ? {
              name: `keywords`,
              content: METADATA.keyWords.join(`, `),
            }
          : []
      )
    }
  />
)

export default MetaTags;
